uint32_t vertices_filter(float* data, float* vertices, uint8_t* colors, uint32_t length) {
    uint32_t i, n = 0;
    uint8_t intensity;
    for (i = 0; i < length; i++) {
        vertices[i*3] = data[i*4];
        vertices[i*3+1] = data[i*4+1];
        vertices[i*3+2] = data[i*4+2];
        intensity = (uint8_t)(data[i*4+3]);
        if (intensity < 256) {
            colors[i*4] = colors_rgba[intensity][0];
            colors[i*4+1] = colors_rgba[intensity][1];
            colors[i*4+2] = colors_rgba[intensity][2];
            colors[i*4+3] = colors_rgba[intensity][3];
        } else {
            colors[i*4] = 255;
            colors[i*4+1] = 0;
            colors[i*4+2] = 0;
            colors[i*4+3] = 255;
        }
        n++;
    }
    return n;
}
